#define ALIGN(bitlength, alignment) \
    (((bitlength != 0)?(((bitlength - 1) / alignment) + 1):0) * \
          (alignment / 8))

unsigned char *option;
size_t src_len_index = 8 + ALIGN(option[2], 8);
size_t total_byte_length = ALIGN((src_len_index + 1) * 8
                           + option[src_len_index], 64);
